import { all, call, takeLatest, put } from "redux-saga/effects";
import {
  auth,
  googleProvider,
  createUserProfileDocument
} from "../../firebase/firebase.utils";
import UserActionTypes from "./user.types";
import { signInFailure, signInSuccess } from "./user.action";

function* googleSignInStart() {
  try {
    const { user } = yield auth.signInWithPopup(googleProvider);
    const userRef = yield call(createUserProfileDocument, user);
    const userSnapshot = yield userRef.get();
    console.log(user);
    yield put(signInSuccess({ id: userSnapshot.id, ...userSnapshot.data() }));
  } catch (e) {
    yield put(signInFailure(e.message));
  }
}
function* emailSignInStart({ payload: { email, password } }) {
  try {
    const { user } = yield auth.signInWithEmailAndPassword(email, password);
    const userRef = yield call(createUserProfileDocument, user);
    const userSnapshot = yield userRef.get();
    yield put(signInSuccess({ id: userSnapshot.id, ...userSnapshot.data() }));
  } catch (e) {
    yield put(signInFailure(e.message));
  }
}

function* signUpStart({ payload: data }) {
  console.log(data);
  // try {
  //   const { user } = yield auth.createUserWithEmailAndPassword(
  //     data.email,
  //     data.password
  //   );
  //   yield createUserProfileDocument(user, { displayName: data.displayName });
  // } catch (e) {
  //   yield put(signInFailure(e.message));
  // }
}

function* onSignUpStart() {
  yield takeLatest(UserActionTypes.REGISTRATION_START, registrationStart);
}

function* onEmailSignInStart() {
  yield takeLatest(UserActionTypes.EMAIL_SIGN_IN_START, emailSignInStart);
}

function* onGoogleSignInStart() {
  yield takeLatest(UserActionTypes.GOOGLE_SIGN_IN_START, googleSignInStart);
}

function* userSaga() {
  yield all([call(onGoogleSignInStart), call(onEmailSignInStart)]);
}

export default userSaga;
