import { all, call, takeLatest, put } from "redux-saga/effects";
import { auth, signInWithGoogle } from "../../firebase/firebase.utils";
import UserActionTypes from "./user.types";

function* googleSignInStart() {
  try {
    // Pedir autenticacion.
    // Guardar objeto autenticado y pasarlo a la accion success
  } catch (e) {
    // Llamar la accion failure
  }
}

function* onGoogleSignInStart() {
  yield takeLatest(UserActionTypes.GOOGLE_SIGN_IN_START, googleSignInStart);
}

function* userSaga() {
  all([call(onGoogleSignInStart)]);
}

export default userSaga;
